
public class StringImmutable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String s1="Mahesh";
		System.out.println("----------------:"+s1);
		
		System.out.println(s1.hashCode());
		
		System.out.println(s1+"surya");
		
		System.out.println(s1.hashCode());
		

	}

}
